<script	type="text/javascript" src="<?php echo base_url('web/js/My97DatePicker/WdatePicker.js'); ?>"></script><script>	function mySub(){		var keyword = document.getElementById("keyword").value;		document.location.href="<?=base_url("C_media/index?keyword=")?>"+keyword;	}	function delaaa(id){		if(confirm('确认删除该媒体信息？')){			document.location.href="<?=base_url("C_media/del_media")?>?id="+id;		}	}	function del(id){		$name = $("#del"+id+" td:nth-child(1)").html();		parent.layer.confirm('删除后将无法恢复，真的要删除 '+$name+' 吗？', {			btn: ['确认','取消'], //按钮			shade: [0.4, '#393D49'] //显示遮罩		}, function(){			$.post("<?=base_url('C_media/del_media')?>?id="+id,function(data){				if(data.code == 0){					parent.layer.msg('删除成功', { icon: 1, time: 1000 }, function(){						location.reload();					});				}else{					parent.layer.msg('删除失败', {icon: 2, time: 2000 });				}			}, "json");		},function(){		});	}</script><div class="row wrapper border-bottom white-bg page-heading">  <div class="col-lg-10">	  <h2>媒体管理</h2>	  <div class="breadcrumb">	    <li>媒体管理</li>	    <li>媒体列表</li>	  </div>  </div></div><br/><div class="ibox-content main_content_margin">	<div class="form-inline">		<?if(check_auth('C_media/add_media')):?>		<div class="form-group">			<a class="btn btn-primary btn-sm page-action" title="媒体添加" href="<?=base_url('C_media/add_media')?>"><i class="fa fa-plus-circle"></i>&nbsp;添加</a>		</div>		<?endif;?>		<div class="form-group">			<div class="input-group">				<input type="text" id="keyword" value="<?=$keyword?>" class="input-sm form-control" placeholder="输入媒体名称" />				<span class="input-group-btn">					<button class="btn btn-primary btn-sm" onclick="mySub();">查询</button>				</span>			</div>		</div>	</div>    <table class="table table-hover table-condensed">        <thead>        <tr>            <th>#</th>            <th>媒体名称</th>            <th>媒体编码</th>            <th>状态</th>            <th>添加时间</th>            <th>备注</th>            <th>操作</th>        </tr>        </thead>		<?php foreach ($media as $row): ?>		<tr id="del<?=$row->id ?>">			<td><?=$row->id ?></td>			<td><?=$row->media_name ?></td>			<td><?=$row->identifier ?></td>			<td><?=$row->status==1?'启用':'禁用';?></td>			<td><?=$row->add_time ?></td>			<td><?=$row->remark ?></td>			<td>				<div class="btn-group">				  	<a class="btn btn-default btn-xs page-action" title="媒体修改" href="<?=base_url('C_media/alter_media?id=').$row->id?>">				  		<i class="fa fa-pencil-square-o"></i>&nbsp;修改					</a>				  	<a class="btn btn-default btn-xs" href="javascript:del(<?=$row->id?>)">				  		<i class="fa fa-trash-o"></i>&nbsp;删除				  	</a>				</div>      </td>		</tr>		<?php endforeach; ?>    </table>    <?php echo $page; ?></div>